  </header>
</template>
<script>
import CustomSelect from '~/components/global/header/CustomSelect';

import { mapActions, mapState } from 'vuex';

export default {
  components: {
    'custom-select': CustomSelect
  },

  data() {
    return {
      active: false,
      curLang: this.$i18n.locale,
      searchActive: false,
      swiperOption: {
        slidesPerView: 'auto',
        init: false,
        observer: true,
        observeParents: true
      },
      leftArrowActive: false,
      rightArrowActive: false,
      langsOpen: false,
      activeMobileTabLink: '/'
    }
  },

  mounted() {
    this.$moment.locale(this.curLang);

    this.mySwiper.on('transitionStart', () => {
      this.updateArrows();
    });

    this.mySwiper.init(this.swiperOption);
    this.updateArrows();
  },

  methods: {
    updateArrows() {
      if (this.mySwiper) {
        this.leftArrowActive = !this.mySwiper.isBeginning;
        this.rightArrowActive = !this.mySwiper.isEnd;
      }
    },

    changeLang() {
      this.$router.push(this.switchLocalePath(this.curLang));
      this.$moment.locale(this.curLang);
    },

    toggleSearch() {
      this.searchActive = !this.searchActive;
    },

    hideSearch() {
      this.searchActive = false;
    },

    goToNewsCat(cat) {
      // :to="`/news?type=${item.slug}`"
      this.$router.push('/news', {query: {
        type: cat
      }});
    }
  },

  computed: {
    ...mapState('news', ['banners', 'cats']),

    availableLangs() {
      return this.$i18n.locales.filter(i => i.code !== this.$i18n.locale);
    }
  }
}

</script>
